use crate::common;

use polars::prelude::*;
use polars_excel_writer::PolarsXlsxWriter;
use rust_xlsxwriter::XlsxError;

// Test case to compare dataframe output against an Excel file.
fn create_new_xlsx_file(filename: &str) -> Result<(), XlsxError> {
    let df: DataFrame = df!(
        "Foo" => &[1, 1, 1],
        "Bar" => &[2, 2, 2],
    )
    .unwrap();

    let mut xl = PolarsXlsxWriter::new();
    xl.write_dataframe_to_cell(&df, 1, 1).unwrap();
    xl.write_excel(filename).unwrap();

    Ok(())
}

#[test]
fn dataframe_write_excel03() {
    let test_runner = common::TestRunner::new()
        .set_name("dataframe03")
        .set_function(create_new_xlsx_file)
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}
